import pandas as pd
from projtools import clean

def test_ward():
    """Test that the ward datasets are in the expected format,
    with the expected columns and the expected sum of crimes"""
    assert clean.ward.iloc[:,5:].sum().sum() == 10701946
    assert clean.ward_10_21.iloc[:,5:].sum().sum() == 8988243
